// Override automatic date setting
msg.Date = "";

// Add a header line.
msg.Header.Add(HeaderLabelType.Organization, "My Organization");

// Now add a custom header line.
msg.Header.Add("X-My-Field: My Value");

// Specify the server
smtp1.Server = "mail.test.com";

// Send the message
smtp1.Send(msg);

