for(int i=0; i<iterations; i++)
   udp1.send(s, "MyEchoServer", 7);

// receive the echoed datagrams, there should be 3...requiring 3 Udp.Receive calls.
for(int i=0; i<iterations; i++)
{
   Datagram d = udp1.Receive(s.Length);
